export const products = generateProducts();

export const getProductsByCategory = (categoryId: string) => {
  return products.filter(p => p.category === categoryId);
};

export const getProductsBySubcategory = (categoryId: string, subcategory: string) => {
  return products.filter(p => p.category === categoryId && p.subcategory === subcategory);
};

export const searchProducts = (query: string, lang: Language = 'en') => {
  const lowerQuery = query.toLowerCase();
  return products.filter(p => 
    p.name[lang].toLowerCase().includes(lowerQuery) ||
    p.subcategory.toLowerCase().includes(lowerQuery) ||
    p.specs.toLowerCase().includes(lowerQuery)
  );
};

export const getProductById = (id: string) => {
  return products.find(p => p.id === id);
};
